def bubble_sort(nums):
n = len(nums)
# 外层循环,控制总的交换轮数
for i in range(n):
# 内层循环,每轮进行相邻交换
for j in range(1, n-i):
if nums[j] < nums[j-1]:
nums[j], nums[j-1] = nums[j-1], nums[j]
return nums

nums = [5, 2, 3, 1, 4]
print(bubble_sort(nums))

